#include<string>
#include<vector>
#include<map>
#include<iostream>
using namespace std;

/* **********
 * 算法思想：使用一个滑动窗口，统计滑动窗口内各字母的出现次数，如果与待匹配字符串一样，为一个解，
 滑动至下一格只需要使次上一个窗口头的字符数-1，再使新滑动到的窗口末字符数+1
 * 字符串s长度为n，p长度为m
 * 时间复杂度O((n-m)*m), 空间复杂度O(m)
 * **********/


class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
        vector<int> output = {};
        map<char, int> sourceCount, desCount;
        int i, lenp = p.size(), lens = s.size();
        if(lenp > lens)
            return output;

        //统计p中单词类别并初始化s中第一个滑动窗口, O(m)
        for(i = 0;i < lenp;i++){
            // if(sourceCount.find(p[i]) == sourceCount.end())
            //     sourceCount[p[i]] = 1;
            // else
            //     sourceCount[p[i]]++;
            sourceCount[p[i]]++;
            desCount[s[i]]++;
        }
        //初始化q中单词类别
        // for(i = 0;i < lenp-1;i++){
        //     if(desCount.find(s[i]) == desCount.end())
        //         desCount[s[i]] = 1;
        //     else
        //         desCount[s[i]]++;
        // }
        //比较
        for(i = 0;i < lens-lenp+1;i++){
            if(sourceCount == desCount)    // 时间复杂度O(n)
                output.emplace_back(i);
            int index = i + lenp;
            desCount[s[index]]++;
            desCount[s[i]]--;
            if(desCount[s[i]] == 0)
                desCount.erase(s[i]);
        }
        return output;
    }
};


int main(){
    // string s = "";
    // string p= "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz";
    //string s = "cbaebabacd", p = "abc";
    string s = "abab", p = "ab";
    Solution solve;
    vector<int> output = solve.findAnagrams(s, p);
    return 0;
}

