#include<string>
#include<vector>
#include<iostream>
using namespace std;

/* **********
 * 算法思想：438_1中，每次滑动滑动窗口都需要比较两个map是否相等
 改进方法：使用差分法存储不同的字符个数，初始化时，使用一个count数组来存储每个字符在滑动窗口和p的差异，
 p中的字符会使count[i]--，滑动窗口中的字符会使count[i]++，如果初始化后count[i]==0，表示字符i在
 滑动窗口和p中无差异，最后统计所有count[i]的绝对值表示第一个滑动窗口和p的总差异，滑动窗口时也只需要根
 据count[i]的变化修改differ，最后通过differ是否为0判断是否是异位词
 * 字符串s长度为n，p长度为m
 * 时间复杂度O(n+m), 空间复杂度O(m)
 * **********/


//解法二： 利用差分法存储不同的字符个数
class Solution {
public:
    vector<int> findAnagrams(string s, string p) {
        int lenp = p.size(), lens = s.size();
        if(lenp > lens)
            return {};
        vector<int> output;
        // unordered_map<char, int> sourceCount, desCount;
        int count[26] = {0}, differ = 0, head_index, tail_index, i;
        //统计p、q中单词类别
        for(i = 0;i < lenp;i++){
            count[p[i]-'a']--;
            count[s[i]-'a']++;
        }
        for(i = 0;i < 26;i++)
            differ += abs(count[i]);

        for(i = 0;i < lens-lenp;i++){
            if(differ == 0)
                output.emplace_back(i);
            head_index = s[i] - 'a';
            char sss = s[i + lenp];
            //s = "abab", p = "ab";结束时，头=1，尾=3
            tail_index = s[i+lenp] -'a';
            //删除第i个元素
            count[head_index]--;
            if(count[head_index] >= 0)
                differ--;
            else   
                differ++;
            count[tail_index]++;
            if(count[tail_index] <= 0)
                differ--;
            else   
                differ++;
        }
        if(differ == 0)
            output.emplace_back(i);
        return output;
    }
};


int main(){
    // string s = "";
    // string p= "vxqakfyaqahufxfizupjrkkifjlbfqfeprqrfjvxnubntdtlvz";
    //string s = "cbaebabacd", p = "abc";
    string s = "abab", p = "ab";
    Solution solve;
    vector<int> output = solve.findAnagrams(s, p);
    return 0;
}

